mapper.insert:
IMPORT INTO can_data_${subTable} USING can_data TAGS (#{carrierId},#{deviceId},#{driverId}) VALUES

  (#{item.ts}, #{item.rfid}, #{item.hourOfCreatedAt}, #{item.avgVelocity}, #{item.distance}, #{item.absDistance}, #{item.movingTime}, #{item.batteryVolt}, #{item.batteryCurrent}, #{item.batteryState}, #{item.brakeTime}, #{item.seatTime}, #{item.forkTime}, #{item.overlapTime}, #{item.forkCounter}, #{item.moveCounter}, #{item.forwardCounter}, #{item.reverseCounter}, #{item.directionChangeCounter}, #{item.forwardTime}, #{item.reverseTime}, #{item.forwardDistance}, #{item.reverseDistance}, #{item.carryTime}, #{item.carryCount}, #{item.turnOnTime})

;

mapper.select:
SELECT * FROM can_data

  and carrier_id=#{carrierId,jdbcType=VARCHAR}
  and device_id=#{deviceId,jdbcType=VARCHAR}
  and driver_id=#{driverId,jdbcType=VARCHAR}
  and #{startTime,jdbcType=BIGINT}<=ts
  and ts<#{endTime,jdbcType=BIGINT}

LIMIT #{pageSize,jdbcType=INTEGER}
OFFSET ${pageIndex*pageSize}

select max(avg_velocity) as maxAvgVelocity,min(avg_velocity) as minAvgVelocity,last(avg_velocity) as avgVelocity, sum(distance) as distance, sum(abs_distance) as absDistance, sum(moving_time) as movingTime, last(battery_volt) as batteryVolt, last(battery_current) as batteryCurrent, last(battery_state) as batteryState, sum(brake_time) as brakeTime, sum(seat_time) as seatTime, sum(fork_time) as forkTime, sum(overlap_time) as overlapTime, sum(fork_counter) as forkCounter, sum(move_counter) as moveCounter, sum(forward_counter) as forwardCounter, sum(reverse_counter) as reverseCounter, sum(direction_change_counter) as directionChangeCounter, sum(forward_time) as forwardTime, sum(reverse_time) as reverseTime, sum(forward_distance) as forwardDistance, sum(reverse_distance) as reverseDistance, sum(carry_time) as carryTime, sum(carry_count) as carryCount, sum(turn_on_time) as turnOnTime from can_data

  and carrier_id=#{carrierId,jdbcType=VARCHAR}
  and device_id=#{deviceId,jdbcType=VARCHAR}
  and driver_id=#{driverId,jdbcType=VARCHAR}
  and #{startTime,jdbcType=BIGINT}<=ts
  and ts<#{endTime,jdbcType=BIGINT}

LIMIT #{pageSize,jdbcType=INTEGER}
OFFSET ${pageIndex*pageSize}

select last(avg_velocity) as avgVelocity, sum(distance) as distance, sum(abs_distance) as absDistance, sum(moving_time) as movingTime, last(battery_volt) as batteryVolt, last(battery_current) as batteryCurrent, last(battery_state) as batteryState, sum(brake_time) as brakeTime, sum(seat_time) as seatTime, sum(fork_time) as forkTime, sum(overlap_time) as overlapTime, sum(fork_counter) as forkCounter, sum(move_counter) as moveCounter, sum(forward_counter) as forwardCounter, sum(reverse_counter) as reverseCounter, sum(direction_change_counter) as directionChangeCounter, sum(forward_time) as forwardTime, sum(reverse_time) as reverseTime, sum(forward_distance) as forwardDistance, sum(reverse_distance) as reverseDistance, sum(carry_time) as carryTime, sum(carry_count) as carryCount, sum(turn_on_time) as turnOnTime from can_data

  and carrier_id=#{carrierId,jdbcType=VARCHAR}
  and device_id=#{deviceId,jdbcType=VARCHAR}
  and driver_id=#{driverId,jdbcType=VARCHAR}
  and #{startTime,jdbcType=BIGINT}<=ts
  and ts<#{endTime,jdbcType=BIGINT}

INTERVAL(1h) FILL('NULL')
LIMIT #{pageSize,jdbcType=INTEGER}
OFFSET ${pageIndex*pageSize}

SELECT

  min(${queryField}) as ${queryField}
  max(${queryField}) as ${queryField}

FROM can_data

  and carrier_id=#{carrierId,jdbcType=VARCHAR}
  and device_id=#{deviceId,jdbcType=VARCHAR}
  and driver_id=#{driverId,jdbcType=VARCHAR}
  and #{startTime,jdbcType=BIGINT}<=ts
  and ts<#{endTime,jdbcType=BIGINT}

SELECT * FROM can_data

  and ts=#{ts,jdbcType=BIGINT}
  and carrier_id=#{carrierId,jdbcType=VARCHAR}
  and device_id=#{deviceId,jdbcType=VARCHAR}
  and driver_id=#{driverId,jdbcType=VARCHAR}

SELECT LAST(avg_velocity) as avgVelocity FROM can_data

  and carrier_id=#{carrierId,jdbcType=VARCHAR}
  and #{startTime,jdbcType=BIGINT}<=ts
  and ts<#{endTime,jdbcType=BIGINT}

INTERVAL(15m) FILL('NULL')

SELECT sum(turn_on_time) FROM can_data

  and carrier_id=#{carrierId,jdbcType=VARCHAR}
  and device_id=#{deviceId,jdbcType=VARCHAR}
  and driver_id=#{driverId,jdbcType=VARCHAR}
  and #{startTime,jdbcType=BIGINT}<=ts
  and ts<#{endTime,jdbcType=BIGINT}

SELECT sum(turn_on_time) as turnOnTime FROM can_data

  and carrier_id=#{carrierId,jdbcType=VARCHAR}
  and device_id=#{deviceId,jdbcType=VARCHAR}
  and driver_id=#{driverId,jdbcType=VARCHAR}
  and #{startTime,jdbcType=BIGINT}<=ts
  and ts<#{endTime,jdbcType=BIGINT}

INTERVAL(1h) FILL('NULL')
